set-variable.value:
@{
                try {
                    var token = (string)context.Variables["pii-msi-access-token"];
                    return string.IsNullOrEmpty(token) ? "empty" : "acquired";
                } catch {
                    return "failed";
                }
            }

send-request.set-body:
@{
                    var inputText = context.Variables.GetValueOrDefault<string>("piiInputContent", "");
                    var language = context.Variables.GetValueOrDefault<string>("piiDetectionLanguage", "en");

                    var excludedCategoriesJson = context.Variables.GetValueOrDefault<string>("piiExcludedCategories", "[]");
                    JArray excludedCategories;
                    try {
                        excludedCategories = JArray.Parse(excludedCategoriesJson);
                    } catch {
                        excludedCategories = new JArray();
                    }

                    var documents = new JArray();
                    try {
                        var bodyObj = JObject.Parse(inputText);
                        var messages = bodyObj["messages"] as JArray;
                        if (messages != null) {
                            for (int i = 0; i < messages.Count && i < 25; i++) {
                                var content = messages[i]?["content"]?.ToString();
                                if (!string.IsNullOrWhiteSpace(content)) {
                                    documents.Add(new JObject {
                                        {"text", content},
                                        {"id", i.ToString()},
                                        {"language", language}
                                    });
                                }
                            }
                        }
                    } catch { /* JSON parse failed - fall through to raw fallback */ }

                    if (documents.Count == 0) {
                        documents.Add(new JObject {
                            {"text", inputText},
                            {"id", "raw"},
                            {"language", language}
                        });
                    }

                    var request = new JObject();
                    request["kind"] = "PiiEntityRecognition";
                    var parameters = new JObject {
                        {"modelVersion", "latest"},
                        {"redactionPolicy", new JObject {
                            {"policyKind", "CharacterMask"},
                            {"redactionCharacter", "#"}
                        }}
                    };
                    if (excludedCategories.Count > 0) {
                        parameters["excludePiiCategories"] = excludedCategories;
                    }
                    request["parameters"] = parameters;
                    request["analysisInput"] = new JObject {
                        {"documents", documents}
                    };
                    return request.ToString();
                }

set-variable.value:
@{
                try {
                    var response = context.Variables.GetValueOrDefault<IResponse>("piiAnalysisResponse");
                    return response != null ? ((int)response.StatusCode).ToString() : "no-response";
                } catch {
                    return "error";
                }
            }

@{
                try {
                    var statusCode = context.Variables.GetValueOrDefault<string>("piiDetectionStatusCode", "0");
                    if (statusCode != "200") { return ""; }
                    var response = context.Variables.GetValueOrDefault<IResponse>("piiAnalysisResponse");
                    if (response == null) { return ""; }
                    return response.Body.As<string>(preserveContent: true);
                } catch (Exception ex) {
                    return "";
                }
            }

@{
                try {
                    var startTicks = long.Parse(context.Variables.GetValueOrDefault<string>("piiStartTimeTicks", DateTime.UtcNow.Ticks.ToString()));
                    return ((DateTime.UtcNow.Ticks - startTicks) / TimeSpan.TicksPerMillisecond).ToString();
                } catch {
                    return "-1";
                }
            }

@{
                try {
                    var statusCode = context.Variables.GetValueOrDefault<string>("piiDetectionStatusCode", "0");
                    if (statusCode != "200") { return "false"; }
                    var bodyStr = context.Variables.GetValueOrDefault<string>("piiResponseBodyStr", "");
                    if (string.IsNullOrEmpty(bodyStr)) { return "false"; }
                    var responseBody = JObject.Parse(bodyStr);
                    var docs = responseBody.SelectToken("$.results.documents") as JArray;
                    if (docs != null) {
                        foreach (var doc in docs) {
                            if (doc["redactedText"] != null) { return "true"; }
                        }
                    }
                    return "false";
                } catch {
                    return "false";
                }
            }

metadata.value:
@{
                            var msi = context.Variables.GetValueOrDefault<string>("piiMsiTokenStatus", "unknown");
                            if (msi == "failed" || msi == "empty") { return "msi-token-" + msi; }
                            var status = context.Variables.GetValueOrDefault<string>("piiDetectionStatusCode", "unknown");
                            if (status == "no-response") { return "network-or-dns-error"; }
                            if (status == "error") { return "response-read-error"; }
                            if (status != "200") { return "http-error-" + status; }
                            return "parse-error";
                        }

return-response.set-body:
@{
                            var piiStatus = context.Variables.GetValueOrDefault<string>("piiDetectionStatusCode", "unknown");
                            var duration = context.Variables.GetValueOrDefault<string>("piiDurationMs", "unknown");
                            var msi = context.Variables.GetValueOrDefault<string>("piiMsiTokenStatus", "unknown");
                            string failureReason;
                            if (msi == "failed" || msi == "empty") { failureReason = "msi-token-" + msi; }
                            else if (piiStatus == "no-response") { failureReason = "network-or-dns-error"; }
                            else if (piiStatus == "error") { failureReason = "response-read-error"; }
                            else if (piiStatus != "200") { failureReason = "http-error-" + piiStatus; }
                            else { failureReason = "parse-error"; }
                            var errorResponse = new JObject {
                                {"error", new JObject {
                                    {"code", "PiiRedactionUnavailable"},
                                    {"message", "PII redaction service is unavailable. Request blocked for data protection."},
                                    {"request_id", context.RequestId.ToString()},
                                    {"pii_status_code", piiStatus},
                                    {"pii_duration_ms", duration},
                                    {"failure_reason", failureReason}
                                }}
                            };
                            return errorResponse.ToString();
                        }

set-variable.value:
@{
                try {
                    var statusCode = context.Variables.GetValueOrDefault<string>("piiDetectionStatusCode", "0");
                    if (statusCode != "200") {
                        return context.Variables.GetValueOrDefault<string>("piiInputContent", "");
                    }
                    var bodyStr = context.Variables.GetValueOrDefault<string>("piiResponseBodyStr", "");
                    if (string.IsNullOrEmpty(bodyStr)) {
                        return context.Variables.GetValueOrDefault<string>("piiInputContent", "");
                    }
                    var responseBody = JObject.Parse(bodyStr);
                    var resultDocs = responseBody.SelectToken("$.results.documents") as JArray;
                    var redactedMap = new System.Collections.Generic.Dictionary<string, string>();
                    if (resultDocs != null) {
                        foreach (var doc in resultDocs) {
                            var id = doc["id"]?.ToString();
                            var redacted = doc["redactedText"]?.ToString();
                            if (id != null && redacted != null) {
                                redactedMap[id] = redacted;
                            }
                        }
                    }
                    if (redactedMap.ContainsKey("raw")) {
                        return redactedMap["raw"];
                    }
                    var inputText = context.Variables.GetValueOrDefault<string>("piiInputContent", "");
                    var bodyObj = JObject.Parse(inputText);
                    var messages = bodyObj["messages"] as JArray;
                    if (messages != null) {
                        for (int i = 0; i < messages.Count; i++) {
                            var key = i.ToString();
                            if (redactedMap.ContainsKey(key)) {
                                ((JObject)messages[i])["content"] = redactedMap[key];
                            }
                        }
                    }
                    return bodyObj.ToString(Newtonsoft.Json.Formatting.None);
                } catch {
                    return context.Variables.GetValueOrDefault<string>("piiInputContent", "");
                }
            }

@{
                try {
                    var statusCode = context.Variables.GetValueOrDefault<string>("piiDetectionStatusCode", "0");
                    if (statusCode != "200") {
                        return "{}";
                    }
                    var bodyStr = context.Variables.GetValueOrDefault<string>("piiResponseBodyStr", "");
                    if (string.IsNullOrEmpty(bodyStr)) { return "{}"; }
                    var responseBody = JObject.Parse(bodyStr);
                    var docs = responseBody.SelectToken("$.results.documents") as JArray;
                    var errors = responseBody.SelectToken("$.results.errors") as JArray;
                    int docCount = docs?.Count ?? 0;
                    int totalEntities = 0;
                    var categories = new System.Collections.Generic.HashSet<string>();
                    if (docs != null) {
                        foreach (var doc in docs) {
                            var entities = doc["entities"] as JArray;
                            int eCount = entities?.Count ?? 0;
                            totalEntities += eCount;
                            if (entities != null) {
                                foreach (var e in entities) {
                                    var cat = e["category"]?.ToString();
                                    if (cat != null) { categories.Add(cat); }
                                }
                            }
                        }
                    }
                    var docErrorsList = new System.Collections.Generic.List<string>();
                    if (errors != null && errors.Count > 0) {
                        foreach (var err in errors) {
                            var id = err["id"]?.ToString() ?? "?";
                            var msg = err.SelectToken("$.error.message")?.ToString() ?? "unknown";
                            docErrorsList.Add("doc-" + id + ":" + msg);
                        }
                    }
                    var result = new JObject {
                        {"docCount", docCount},
                        {"docErrors", docErrorsList.Count > 0 ? string.Join("; ", docErrorsList) : "none"},
                        {"entityCount", totalEntities},
                        {"entityTypes", categories.Count > 0 ? string.Join(",", categories) : "none"}
                    };
                    return result.ToString(Newtonsoft.Json.Formatting.None);
                } catch (Exception ex) {
                    return "{}";
                }
            }

@{
                try {
                    var inputText = context.Variables.GetValueOrDefault<string>("piiInputContent", "");
                    var anonymized = context.Variables.GetValueOrDefault<string>("piiAnonymizedContent", "");
                    return (!string.Equals(inputText, anonymized, System.StringComparison.Ordinal)).ToString();
                } catch {
                    return "error";
                }
            }
