send-request.set-body:
@{
                    var inputText = context.Variables.GetValueOrDefault<string>("piiInputContent", "");
                    var request = new JObject();
                    request["kind"] = "PiiEntityRecognition";
                    request["parameters"] = new JObject {
                        {"modelVersion", "latest"},
                        {"redactionPolicy", new JObject {
                            {"policyKind", "CharacterMask"},
                            {"redactionCharacter", "#"}
                        }}
                    };
                    request["analysisInput"] = new JObject {
                        {"documents", new JArray {
                            new JObject {
                                {"text", inputText},
                                {"id", "1"},
                                {"language", "en"}
                            }
                        }}
                    };
                    return request.ToString();
                }

set-variable.value:
@{
                try {
                    var response = context.Variables.GetValueOrDefault<IResponse>("piiAnalysisResponse");
                    if (response == null || response.StatusCode != 200) { return "0"; }
                    var responseBody = JObject.Parse(response.Body.As<string>(preserveContent: true));
                    var entities = responseBody.SelectToken("$.results.documents[0].entities") as JArray;
                    return (entities?.Count ?? 0).ToString();
                } catch { return "error"; }
            }

@{
                try {
                    var response = context.Variables.GetValueOrDefault<IResponse>("piiAnalysisResponse");
                    if (response == null || response.StatusCode != 200) { return "none"; }
                    var responseBody = JObject.Parse(response.Body.As<string>(preserveContent: true));
                    var entities = responseBody.SelectToken("$.results.documents[0].entities") as JArray;
                    if (entities == null || entities.Count == 0) { return "none"; }
                    var categories = new System.Collections.Generic.HashSet<string>();
                    foreach (var entity in entities) {
                        var category = entity["category"];
                        if (category != null) { categories.Add(category.ToString()); }
                    }
                    return string.Join(",", categories);
                } catch { return "error"; }
            }

@{
                var inputText = context.Variables.GetValueOrDefault<string>("piiInputContent", "");
                var anonymized = context.Variables.GetValueOrDefault<string>("piiAnonymizedContent", "");
                return (!string.Equals(inputText, anonymized, System.StringComparison.Ordinal)).ToString();
            }
